import React, { useState, useEffect } from "react";
import "./VotingBox.css";

function VotingBox({ votes, toggleVoteModal, setVotes, user }) {
  const [expandedVoteIndex, setExpandedVoteIndex] = useState(null);
  const [voteCounts, setVoteCounts] = useState([]);
  const [userVotes, setUserVotes] = useState({}); // Ukladáme odpoveď index pre každú anketu

  // Funkcia na získanie kľúča pre localStorage podľa používateľa
  const getUserStorageKey = (key) => {
    if (user && user.id) {  // Skontrolujeme, či user a jeho id existujú
      return `${key}_${user.id}`;  // Spojenie kľúča s ID používateľa
    }
    return key;  // Vrátime len kľúč, ak user nie je definovaný
  };

  // Načítanie indexu rozbaleného hlasovania zo storage
  useEffect(() => {
    if (!user || !user.id) return;  // Ak user nie je definovaný, nevykonávame žiadne operácie

    const savedIndex = localStorage.getItem(getUserStorageKey("expandedVoteIndex"));
    if (savedIndex !== null) {
      setExpandedVoteIndex(JSON.parse(savedIndex));  // Načítame rozbalený index
    }

    // Načítame počty hlasov zo storage alebo ich inicializujeme
    const savedVoteCounts = localStorage.getItem(getUserStorageKey("voteCounts"));
    if (savedVoteCounts) {
      setVoteCounts(JSON.parse(savedVoteCounts));
    } else {
      const initialVoteCounts = votes.map(vote => new Array(vote.answers.length).fill(0));
      setVoteCounts(initialVoteCounts);
      localStorage.setItem(getUserStorageKey("voteCounts"), JSON.stringify(initialVoteCounts));
    }

    // Načítame užívateľské hlasovanie zo localStorage
    const savedUserVotes = localStorage.getItem(getUserStorageKey("userVotes"));
    if (savedUserVotes) {
      setUserVotes(JSON.parse(savedUserVotes));
    }
  }, [votes, user]);  // Tento useEffect sa spustí vždy, keď sa zmení používateľ

  // Funkcia na rozbalenie/zbalenie hlasovania
  const handleVoteClick = (index) => {
    const newExpandedIndex = expandedVoteIndex === index ? null : index;
    setExpandedVoteIndex(newExpandedIndex);
    localStorage.setItem(getUserStorageKey("expandedVoteIndex"), JSON.stringify(newExpandedIndex));
  };

  // Funkcia na hlasovanie
  const handleVote = (voteIndex, answerIndex) => {
    if (!user || !user.id) {  // Skontrolujeme, či je používateľ prihlásený
      alert("Musíte byť prihlásený, aby ste mohli hlasovať.");
      return;
    }

    // Ak už používateľ hlasoval pre túto anketu, nedovolíme ďalší hlas
    if (userVotes[voteIndex] !== undefined && userVotes[voteIndex] !== null) {
      alert("Už ste hlasovali!");
      return;
    }

    const updatedVoteCounts = [...voteCounts];
    if (!updatedVoteCounts[voteIndex]) {
      updatedVoteCounts[voteIndex] = new Array(votes[voteIndex].answers.length).fill(0);
    }

    updatedVoteCounts[voteIndex][answerIndex] += 1;
    setVoteCounts(updatedVoteCounts);
    localStorage.setItem(getUserStorageKey("voteCounts"), JSON.stringify(updatedVoteCounts));

    // Uložíme odpoveď index pre konkrétnu anketu a používateľa
    setUserVotes((prevVotes) => {
      const updatedVotes = { ...prevVotes, [voteIndex]: answerIndex };
      localStorage.setItem(getUserStorageKey("userVotes"), JSON.stringify(updatedVotes));
      return updatedVotes;
    });
  };

  // Funkcia na vymazanie hlasu pre konkrétnu anketu
  const handleRemoveVote = (voteIndex) => {
    if (!user || !user.id) {  // Skontrolujeme, či je používateľ prihlásený
      alert("Musíte byť prihlásený, aby ste mohli odstrániť hlas.");
      return;
    }

    if (userVotes[voteIndex] === undefined) {
      alert("Žiadny hlas pre túto anketu nebol odovzdaný.");
      return;
    }

    const updatedVoteCounts = [...voteCounts];
    const answerIndex = userVotes[voteIndex];
    updatedVoteCounts[voteIndex][answerIndex] -= 1;

    setVoteCounts(updatedVoteCounts);
    localStorage.setItem(getUserStorageKey("voteCounts"), JSON.stringify(updatedVoteCounts));

    // Odstránime hlasovanie pre túto konkrétnu anketu
    const updatedUserVotes = { ...userVotes };
    delete updatedUserVotes[voteIndex];
    setUserVotes(updatedUserVotes);
    localStorage.setItem(getUserStorageKey("userVotes"), JSON.stringify(updatedUserVotes));
  };

  // Funkcia na vymazanie hlasovania (vymazanie aj počtov hlasov) pre konkrétnu anketu
  const deleteVote = (voteIndex) => {
    if (!user || !user.id) {  // Skontrolujeme, či je používateľ prihlásený
      alert("Musíte byť prihlásený, aby ste mohli vymazať hlasovanie.");
      return;
    }

    // Odstránenie hlasovania zo zoznamu
    const updatedVotes = votes.filter((_, index) => index !== voteIndex);
    setVotes(updatedVotes);
    localStorage.setItem("votes", JSON.stringify(updatedVotes));

    // Odstránenie počtov hlasov pre dané hlasovanie
    const updatedVoteCounts = voteCounts.filter((_, index) => index !== voteIndex);
    setVoteCounts(updatedVoteCounts);
    localStorage.setItem(getUserStorageKey("voteCounts"), JSON.stringify(updatedVoteCounts));

    // Odstránenie hlasovania zo zoznamu používateľských hlasov
    const updatedUserVotes = { ...userVotes };
    delete updatedUserVotes[voteIndex];
    setUserVotes(updatedUserVotes);
    localStorage.setItem(getUserStorageKey("userVotes"), JSON.stringify(updatedUserVotes));
  };

  return (
    <div className="voting-box">
      <div className="voting-header">
        <h3>Posledné hlasovanie</h3>
        <button className="add-btn" onClick={toggleVoteModal}>
          <img src="./Add.svg" alt="Add Vote" className="add-icon" />
        </button>
      </div>
      <hr />
      {votes.length > 0 ? (
        votes.map((vote, voteIndex) => (
          <div key={voteIndex}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
              <h4
                onClick={() => handleVoteClick(voteIndex)} // Kliknutím na názov sa hlasovanie rozbalí/zbalí
                style={{ cursor: "pointer", color: "blue" }}
              >
                {vote.title}
              </h4>
              <button
                onClick={() => deleteVote(voteIndex)} // Vymazanie hlasovania
                style={{
                  background: "red",
                  color: "white",
                  border: "none",
                  padding: "5px 10px",
                  cursor: "pointer"
                }}
              >
                Vymazať
              </button>
            </div>

            {/* Ak je hlasovanie rozbalené, zobrazí sa popis a odpovede */}
            {expandedVoteIndex === voteIndex && (
              <div>
                <p>{vote.description}</p>
                <ul className="voting-results">
                  {vote.answers.map((answer, answerIndex) => (
                    <li key={answerIndex}>
                      <button
                        onClick={() => handleVote(voteIndex, answerIndex)} // Na kliknutie hlasujeme
                        style={{ marginRight: "10px" }}
                        disabled={userVotes[voteIndex] !== undefined} // Zablokovanie tlačidla, ak používateľ už hlasoval v tejto ankete
                      >
                        {answer}
                      </button>
                      {/* Zobrazenie počtu hlasov pre každú odpoveď */}
                      {voteCounts[voteIndex] && voteCounts[voteIndex][answerIndex] !== undefined ? (
                        <span>{voteCounts[voteIndex][answerIndex]} hlasov</span>
                      ) : (
                        <span>0 hlasov</span>
                      )}
                      {/* Zobrazenie, že používateľ už hlasoval za danú možnosť */}
                      {userVotes[voteIndex] === answerIndex && (
                        <span style={{ color: "green", marginLeft: "10px" }}>
                          Tento hlas ste už odovzdali
                        </span>
                      )}
                    </li>
                  ))}
                </ul>

                {/* Tlačidlo na vymazanie hlasu pre túto konkrétnu anketu */}
                {userVotes[voteIndex] !== undefined && (
                  <button
                    onClick={() => handleRemoveVote(voteIndex)}
                    style={{
                      marginTop: "10px",
                      backgroundColor: "orange",
                      color: "white",
                      padding: "8px 15px",
                      border: "none",
                      cursor: "pointer",
                    }}
                  >
                    Vymazať môj hlas
                  </button>
                )}
              </div>
            )}
          </div>
        ))
      ) : (
        <p>Žiadne hlasovanie ešte nebolo pridané.</p>
      )}
    </div>
  );
}

export default VotingBox;
