import React, { useState, useEffect } from "react";
import "./VotingBox.css";

function VotingBox({ votes, toggleVoteModal }) {
  // Načítame index rozbaleného hlasovania zo localStorage, ak existuje
  const [expandedVoteIndex, setExpandedVoteIndex] = useState(null);

  // Stav pre počet hlasov pre každú odpoveď v hlasovaní
  const [voteCounts, setVoteCounts] = useState([]);

  // Stav, ktorý určuje, či už používateľ hlasoval
  const [hasVoted, setHasVoted] = useState(false);

  // Načítame zo storage stav hlasovania a počty hlasov
  useEffect(() => {
    const savedIndex = localStorage.getItem("expandedVoteIndex");
    if (savedIndex !== null) {
      setExpandedVoteIndex(JSON.parse(savedIndex));  // Načítame rozbalený index zo storage
    }

    // Načítame uložené počty hlasov zo storage
    const savedVoteCounts = localStorage.getItem("voteCounts");
    if (savedVoteCounts !== null) {
      setVoteCounts(JSON.parse(savedVoteCounts));  // Načítame počty hlasov zo storage
    } else {
      // Inicializujeme počet hlasov na nulu pre každé hlasovanie a odpoveď
      const initialVoteCounts = votes.map((vote) => new Array(vote.answers.length).fill(0));
      setVoteCounts(initialVoteCounts);
      localStorage.setItem("voteCounts", JSON.stringify(initialVoteCounts));  // Uložíme počiatočné počty
    }

    // Skontrolujeme, či používateľ už hlasoval
    const hasUserVoted = localStorage.getItem("hasVoted");
    if (hasUserVoted === "true") {
      setHasVoted(true);  // Nastavíme stav, že používateľ už hlasoval
    }
  }, [votes]);

  // Funkcia na rozbalenie alebo zbalenie hlasovania
  const handleVoteClick = (index) => {
    const newExpandedIndex = expandedVoteIndex === index ? null : index;
    setExpandedVoteIndex(newExpandedIndex);
    localStorage.setItem("expandedVoteIndex", JSON.stringify(newExpandedIndex));
  };

  // Funkcia na hlasovanie
  const handleVote = (voteIndex, answerIndex) => {
    // Ak už používateľ hlasoval, zablokujeme ďalšie hlasovanie
    if (hasVoted) {
      alert("Už ste hlasovali!");
      return;
    }

    // Skontrolujeme, či sú počiatočné hodnoty pre počet hlasov existujú
    if (!voteCounts[voteIndex]) {
      console.error("Neexistujú počty hlasov pre toto hlasovanie");
      return;
    }

    const updatedVoteCounts = [...voteCounts];
    updatedVoteCounts[voteIndex][answerIndex] += 1;  // Zväčšíme počet hlasov pre túto odpoveď

    setVoteCounts(updatedVoteCounts);
    localStorage.setItem("voteCounts", JSON.stringify(updatedVoteCounts));  // Uložíme nové počty hlasov

    // Uložíme informáciu, že používateľ už hlasoval
    localStorage.setItem("hasVoted", "true");
    setHasVoted(true);  // Nastavíme stav, že používateľ už hlasoval
  };

  // Funkcia na vymazanie hlasovania
  const deleteVote = (voteIndex) => {
    const updatedVotes = votes.filter((_, index) => index !== voteIndex);  // Odstránime hlasovanie podľa indexu
    setVotes(updatedVotes);
    localStorage.setItem("votes", JSON.stringify(updatedVotes));  // Uložíme upravený zoznam hlasovaní

    const updatedVoteCounts = voteCounts.filter((_, index) => index !== voteIndex);  // Odstránime počty hlasov pre dané hlasovanie
    setVoteCounts(updatedVoteCounts);
    localStorage.setItem("voteCounts", JSON.stringify(updatedVoteCounts));  // Uložíme upravené počty hlasov

    // Resetujeme informáciu o hlasovaní (v prípade, že je to potrebné)
    localStorage.removeItem("hasVoted");
    setHasVoted(false);  // Resetujeme stav, že používateľ hlasoval
  };

  const latestVote = votes[votes.length - 1]; // Získame posledné hlasovanie

  return (
    <div className="voting-box">
      <div className="voting-header">
        <h3>Posledné hlasovanie</h3>
        <button className="add-btn" onClick={toggleVoteModal}>
          <img src="./Add.svg" alt="Add Vote" className="add-icon" />
        </button>
      </div>
      <hr />
      {latestVote ? (
        <div>
          <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
            <h4
              onClick={() => handleVoteClick(0)} // Kliknutím na názov sa hlasovanie rozbalí/zbalí
              style={{ cursor: "pointer", color: "blue" }}
            >
              {latestVote.title}
            </h4>
            <button
              onClick={() => deleteVote(0)} // Vymazanie hlasovania pri kliknutí na tlačidlo
              style={{
                background: "red",
                color: "white",
                border: "none",
                padding: "5px 10px",
                cursor: "pointer"
              }}
            >
              Vymazať
            </button>
          </div>

          {/* Ak je hlasovanie rozbalené, zobrazí sa popis a odpovede */}
          {expandedVoteIndex === 0 && (
            <div>
              <p>{latestVote.description}</p>
              <ul className="voting-results">
                {latestVote.answers.map((answer, index) => (
                  <li key={index}>
                    <button
                      onClick={() => handleVote(0, index)} // Na kliknutie hlasujeme
                      style={{ marginRight: "10px" }}
                      disabled={hasVoted} // Zablokovanie tlačidla, ak používateľ už hlasoval
                    >
                      {answer}
                    </button>
                    {/* Zobrazenie počtu hlasov pre každú odpoveď */}
                    {voteCounts[0] && voteCounts[0][index] !== undefined ? (
                      <span>{voteCounts[0][index]} hlasov</span>
                    ) : (
                      <span>0 hlasov</span>
                    )}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      ) : (
        <p>Žiadne hlasovanie ešte nebolo pridané.</p>
      )}
    </div>
  );
}

export default VotingBox;
