import React, { useState, useEffect } from "react";
import "./Modal.css";

function DocumentsModal({ isOpen, toggleModal, refreshDocuments }) {
  const [description, setDescription] = useState("");
  const [type, setType] = useState("plán");
  const [date, setDate] = useState(new Date().toISOString().split("T")[0]);
  const [files, setFiles] = useState([]);

  useEffect(() => {
    if (!isOpen) {
      setDescription("");
      setType("plán");
      setDate(new Date().toISOString().split("T")[0]);
      setFiles([]);
    }
  }, [isOpen]);

  const handleFileChange = (e) => setFiles([...e.target.files]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!description || !date || files.length === 0) {
      alert("Vyplňte všetky polia a pridajte súbor.");
      return;
    }

    const formData = new FormData();
    formData.append("description", description);
    formData.append("type", type);
    formData.append("date", date);
    files.forEach((file) => formData.append("files", file));

    try {
      const response = await fetch("/api/documents", {
        method: "POST",
        body: formData,
      });
      if (response.ok) {
        alert("Dokument úspešne pridaný.");
        refreshDocuments();
        toggleModal();
      } else {
        throw new Error("Chyba pri pridávaní dokumentu.");
      }
    } catch (error) {
      console.error("Error uploading document:", error);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={toggleModal}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="close-btn" onClick={toggleModal}>X</button>
        <h2>Pridať nový dokument</h2>
        <form onSubmit={handleSubmit}>
          <select value={type} onChange={(e) => setType(e.target.value)} required>
            <option value="plán">Plán</option>
            <option value="záznam zo schôdze">Záznam zo schôdze</option>
            <option value="faktúra">Faktúra</option>
          </select>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Popis dokumentu"
            required
          />
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
          <input type="file" onChange={handleFileChange} multiple required />
          <button type="submit">Odošli</button>
        </form>
      </div>
    </div>
  );
}

export default DocumentsModal;
