import React, { useState, useEffect } from "react";
import "./VotingBox.css";

function VotingBox({ votes, toggleVoteModal }) {
  // Načítame index rozbaleného hlasovania zo localStorage, ak existuje
  const [expandedVoteIndex, setExpandedVoteIndex] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState(null); // Stav pre uloženie odpovede používateľa

  // Načítame z localStorage, ak existuje
  useEffect(() => {
    const savedIndex = localStorage.getItem("expandedVoteIndex");
    if (savedIndex !== null) {
      console.log("Načítaná hodnota zo storage:", savedIndex); // Log, aby ste videli, čo načítate
      setExpandedVoteIndex(JSON.parse(savedIndex));  // Načítame rozbalený index zo storage
    }
  }, []); // Spustí sa len pri prvom načítaní komponentu

  // Funkcia na rozbalenie alebo zbalenie hlasovania
  const handleVoteClick = (index) => {
    const newExpandedIndex = expandedVoteIndex === index ? null : index;
    setExpandedVoteIndex(newExpandedIndex);
    // Uložíme index rozbaleného hlasovania do localStorage
    localStorage.setItem("expandedVoteIndex", JSON.stringify(newExpandedIndex)); // Správne uloženie
    console.log("Uložený index:", newExpandedIndex); // Log na overenie ukladania
  };

  // Funkcia na kliknutie na odpoveď, na ktorú sa používateľ rozhodol hlasovať
  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer); // Nastavenie vybranej odpovede
    alert(`Hlasovali ste za odpoveď: ${answer}`); // Môžete upravit, aby sa vykonala nejaká akcia po hlasovaní
  };

  // Predpokladáme, že zoznam hlasovaní má štruktúru, kde každý záznam má title, description a answers
  const latestVote = votes[votes.length - 1]; // Získame posledné hlasovanie

  return (
    <div className="voting-box">
      <div className="voting-header">
        <h3>Posledné hlasovanie</h3>
        <button className="add-btn" onClick={toggleVoteModal}>
          <img src="./Add.svg" alt="Add Vote" className="add-icon" />
        </button>
      </div>
      <hr />
      {latestVote ? (
        <div>
          <h4
            onClick={() => handleVoteClick(0)} // Kliknutím na názov sa hlasovanie rozbalí/zbalí
            style={{ cursor: "pointer", color: "blue" }}
          >
            {latestVote.title}
          </h4>

          {/* Ak je hlasovanie rozbalené, zobrazí sa popis a odpovede ako tlačidlá */}
          {expandedVoteIndex === 0 && (
            <div>
              <p>{latestVote.description}</p>
              <div>
                {latestVote.answers.map((answer, index) => (
                  <button
                    key={index}
                    onClick={() => handleAnswerClick(answer)}
                    style={{
                      margin: "5px",
                      padding: "10px",
                      backgroundColor: "blue", 
                      color: "white", 
                      border: "none",
                      borderRadius: "10px",
                      cursor: "pointer",
                    }}
                  >
                    {answer}
                  </button>
                ))}
              </div>
              {selectedAnswer && (
                <div style={{ marginTop: "10px", fontWeight: "bold" }}>
                  Hlasovali ste za: {selectedAnswer}
                </div>
              )}
            </div>
          )}
        </div>
      ) : (
        <p>Žiadne hlasovanie ešte nebolo pridané.</p>
      )}
    </div>
  );
}

export default VotingBox;
