import React, { useState, useEffect } from "react";
import Header from "./Header";
import Sidebar from "./Sidebar";
import "./Settings.css";

function Settings() {
  const user = JSON.parse(localStorage.getItem("user")) || {};
  const [currentEmail, setCurrentEmail] = useState(user.email || "");
  const [formData, setFormData] = useState({
    newEmail: "",
    oldPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [newUser, setNewUser] = useState({
    name: "",
    lastName: "",
    email: "",
    apartmentNumber: "",
    password: "",
    confirmPassword: "",
  });

  // Načítanie údajov používateľa pri načítaní komponentu
  useEffect(() => {
    if (user && user.email) {
      setCurrentEmail(user.email);
    }
  }, [user]);

  const handleLogout = () => {
    localStorage.removeItem("user");
    window.location.href = "/";
  };

  // Zmena údajov pre existujúceho používateľa
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { newEmail, oldPassword, newPassword, confirmPassword } = formData;

    // Logovanie dát, ktoré sa posielajú na server
    console.log({
      currentEmail,    // Kontrolujte, či je tento údaj správny
      oldPassword,     // Kontrolujte, či je toto staré heslo správne
      newEmail,        // Skontrolujte, či je nový email správny
      newPassword,     // Skontrolujte, či je nové heslo správne
    });

    if (!oldPassword) {
      alert("Staré heslo je potrebné na uloženie zmien!");
      return;
    }

    if (!newEmail && !newPassword) {
      alert("Musíte zadať nový e-mail alebo nové heslo.");
      return;
    }

    if ((newPassword && !confirmPassword) || (!newPassword && confirmPassword)) {
      alert("Musíte potvrdiť heslo.");
      return;
    }

    if (newPassword !== confirmPassword) {
      alert("Heslá sa nezhodujú!");
      return;
    }

    try {
      const response = await fetch('/api/user/update', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json', // Ensure content type is set correctly
        },
        body: JSON.stringify({
          currentEmail,    // The current email of the logged-in user
          oldPassword,
          newEmail,
          newPassword,
        }),
      });

      const data = await response.json();

      if (data.success) {
        alert(data.message);
        // Update the UI if needed, such as updating the `currentEmail`
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Error while updating user details:", error);
      alert("Chyba pri aktualizácii údajov.");
    }

    setFormData({
      newEmail: "",
      oldPassword: "",
      newPassword: "",
      confirmPassword: "",
    });
  };

  // Pridanie nového používateľa
  const handleNewUserChange = (e) => {
    const { name, value } = e.target;
    setNewUser((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddUser = async (e) => {
    e.preventDefault();

    const {
      name,
      lastName,
      email,
      apartmentNumber,
      password,
      confirmPassword,
    } = newUser;

    if (!name || !lastName || !email || !apartmentNumber || !password || !confirmPassword) {
      alert("Všetky polia sú povinné!");
      return;
    }

    if (password !== confirmPassword) {
      alert("Heslá sa nezhodujú!");
      return;
    }

    try {
      const response = await fetch('/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json', // Ensure content type is set correctly
        },
        body: JSON.stringify({
          name,
          lastName,
          email,
          apartmentNumber,
          password,
        }),
      });

      const data = await response.json();

      if (data.success) {
        alert(data.message);
        setNewUser({
          name: "",
          lastName: "",
          email: "",
          apartmentNumber: "",
          password: "",
          confirmPassword: "",
        });
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Error while adding new user:", error);
      alert("Chyba pri pridávaní nového používateľa.");
    }
  };

  return (
    <div className="settings-page">
      <Sidebar />
      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        <div className="settings-content">
          <div className="settings-box">
            <h1>Nastavenia</h1>
            <form className="settings-form" onSubmit={handleSubmit}>
              <div className="form-group">
                <div className="label-wrapper">
                  <label className="label-text">Aktuálny e-mail:</label>
                  <div className="current-email">{currentEmail}</div>
                </div>
                <div className="label-wrapper">
                  <label className="label-text new-email">Nový e-mail:</label>
                  <input
                    type="email"
                    name="newEmail"
                    value={formData.newEmail}
                    onChange={handleInputChange}
                    className="input-field"
                    placeholder="Zadajte nový e-mail"
                  />
                </div>
              </div>
              <div className="form-group">
                <div className="label-wrapper">
                  <label className="label-text">Nové heslo:</label>
                  <input
                    type="password"
                    name="newPassword"
                    value={formData.newPassword}
                    onChange={handleInputChange}
                    className="input-field"
                    placeholder="Zadajte nové heslo"
                  />
                </div>
                <div className="label-wrapper">
                  <label className="label-text">Potvrdenie hesla:</label>
                  <input
                    type="password"
                    name="confirmPassword"
                    value={formData.confirmPassword}
                    onChange={handleInputChange}
                    className="input-field"
                    placeholder="Zopakujte nové heslo"
                  />
                </div>
              </div>
              <div className="label-wrapper">
                <label className="label-text">Staré heslo:</label>
                <input
                  type="password"
                  name="oldPassword"
                  value={formData.oldPassword}
                  onChange={handleInputChange}
                  className="input-field"
                  placeholder="Zadajte staré heslo"
                />
              </div>
              <button type="submit" className="save-btn">
                Uložiť
              </button>
            </form>
          </div>

          <div className="new-user-box">
            <h1>Pridať nového používateľa</h1>
            <form className="new-user-form" onSubmit={handleAddUser}>
              {[
                { label: "Meno", name: "name" },
                { label: "Priezvisko", name: "lastName" },
                { label: "E-mail", name: "email" },
                { label: "Číslo bytu", name: "apartmentNumber" },
                { label: "Heslo", name: "password" },
                { label: "Potvrdenie hesla", name: "confirmPassword" },
              ].map((field) => (
                <div key={field.name} className="label-wrapper">
                  <label className="label-text">{field.label}:</label>
                  <input
                    type={field.name.includes("password") ? "password" : "text"}
                    name={field.name}
                    value={newUser[field.name]}
                    onChange={handleNewUserChange}
                    className="input-field"
                    placeholder={`Zadajte ${field.label.toLowerCase()}`}
                  />
                </div>
              ))}
              <button type="submit" className="save-btn">
                Pridať
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Settings;
