import React, { useState } from "react";
import "./Modal.css";

function InvoiceModal({ isOpen, toggleModal }) {
  const [showTooltip, setShowTooltip] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // ked nie je modal otvoreny, nevykresluje
  if (!isOpen) return null;

  // Funkcia na sledovanie pozicie mysi
  const handleMouseMove = (e) => {
    setMousePosition({ x: e.clientX, y: e.clientY });
  };

  return (
    <div className="modal-overlay" onClick={toggleModal}>
      <div
        className="modal-content faktury-modal"
        onClick={(e) => e.stopPropagation()} // Zabraňuje zatvoreniu modalu pri kliknutí vnútri obsahu
      >
        <h2>Pridať uhradenú faktúru</h2>
        <form>
          <label>
            Dátum úhrady
            <input
              type="date"
              placeholder="Popis objednavky"
              className="input-date"
            />
          </label>
          <label>
            <input
              type="text"
              placeholder="Popis"
              className="input-description"
            />
          </label>
          <label>
            <input
              type="number"
              placeholder="Cena"
              className="input-price"
              min="0" // Zabranuje zadaní záporných čísel
            />
          </label>
          <label>
            <input
              type="text"
              placeholder="IČO dodávateľa"
              className="input-supplier-id"
            />
          </label>

          <div className="button-container">
            {/* Tlacidlo pre nahravanie suboru s tooltipom */}
            <label
              htmlFor="file-upload"
              className="custom-upload-button"
              onMouseEnter={() => setShowTooltip(true)}
              onMouseLeave={() => setShowTooltip(false)}
              onMouseMove={handleMouseMove}
            >
              <img
                src="./Upload.svg"
                alt="Upload Icon"
                className="upload-icon"
              />
              {/* hover tooltip */}
              {showTooltip && (
                <span
                  className="tooltip"
                  style={{
                    top: mousePosition.y + 15,
                    left: mousePosition.x + 15,
                  }}
                >
                  Pridaj fotku faktúry
                </span>
              )}
            </label>

            {/* Skryty vstup pre nahravanie suboru */}
            <input
              type="file"
              id="file-upload"
              className="input-upload"
              style={{ display: "none" }}
            />

            {/* Tlačidlo pre odoslanie formulára */}
            <button type="submit" className="submit-btn">
              Odošli
            </button>
          </div>
        </form>

        {/* Tlačidlo na zatvorenie modalu */}
        <button className="close-btn" onClick={toggleModal}>
          X
        </button>
      </div>
    </div>
  );
}

export default InvoiceModal;
