import React, { useState, useEffect } from "react";
import "./VotingBox.css";

function VotingBox({ votes, setVotes, toggleVoteModal, handleVote }) {
  const [expandedVoteIndex, setExpandedVoteIndex] = useState(null);
  const [showAllVotes, setShowAllVotes] = useState(false);

  const handleVoteClick = (index) => {
    const newExpandedIndex = expandedVoteIndex === index ? null : index;
    setExpandedVoteIndex(newExpandedIndex);
  };

  const handleRemoveVote = (voteIndex) => {
    const userId = localStorage.getItem("userId");

    // Create a copy of the votes array to avoid direct mutation
    const updatedVotes = [...votes];
    const currentVote = updatedVotes[voteIndex];

    // Ensure currentVote is mutable and can be updated
    if (currentVote.userVotes && currentVote.userVotes[userId] !== undefined) {
      const answerIndex = currentVote.userVotes[userId];
      // Decrease vote count
      currentVote.answers[answerIndex].votes -= 1;
      delete currentVote.userVotes[userId]; // Remove user vote

      // Update the state and localStorage with a new copy
      setVotes(updatedVotes);
      localStorage.setItem("votes", JSON.stringify(updatedVotes));
    } else {
      alert("Hlas pre túto anketu neexistuje.");
    }
  };

  const renderVoteCounts = (vote, voteIndex) => (
    <ul className="voting-results">
      {vote.answers.map((answer, answerIndex) => (
        <li key={answerIndex} className="voting-item">
          <div className="answer-container">
            <button
              onClick={() => handleVote(voteIndex, answerIndex)} // Handle vote
              className="square-btn"
            >
              <img
                src={
                  vote.userVotes && vote.userVotes[localStorage.getItem("userId")] === answerIndex
                    ? "./Tick-checkbox.svg"
                    : "./Square.svg"
                }
                alt={
                  vote.userVotes && vote.userVotes[localStorage.getItem("userId")] === answerIndex
                    ? "Checked"
                    : "Unchecked"
                }
              />
            </button>
            <span className="answer-text">{answer.text}</span>
          </div>
          <span className="vote-count">
            {answer.votes || 0} votes
          </span>
        </li>
      ))}
    </ul>
  );

  return (
    <div className="voting-box">
      <div className="voting-header">
        <h3>
          Posledné hlasovanie
          <button className="add-btn" onClick={toggleVoteModal}>
            <img src="./Add.svg" alt="Add Vote" className="icon add-icon" />
          </button>
        </h3>
        <button
          className="list-btn"
          onClick={() => setShowAllVotes(!showAllVotes)}
        >
          <img src="./List.svg" alt="List Votes" className="icon list-icon" />
        </button>
      </div>
      <hr />
      {votes.length > 0 ? (
        showAllVotes ? (
          votes.map((vote, voteIndex) => (
            <div key={voteIndex}>
              <div className="vote-header">
                <h4
                  onClick={() => handleVoteClick(voteIndex)}
                  className="vote-title"
                >
                  {vote.title}
                </h4>
                <button
                  onClick={() => handleRemoveVote(voteIndex)} // Handle remove vote
                  className="delete-btn1"
                >
                  Vymazať
                </button>
              </div>
              {expandedVoteIndex === voteIndex && (
                <div>
                  <p>{vote.description}</p>
                  {renderVoteCounts(vote, voteIndex)}
                </div>
              )}
            </div>
          ))
        ) : (
          <div>
            <div className="vote-header">
              <h4 className="vote-title">{votes[votes.length - 1].title}</h4>
              <button
                onClick={() => handleRemoveVote(votes.length - 1)} // Handle remove vote
                className="delete-btn1"
              >
                Vymazať
              </button>
            </div>
            <div>
              <p>{votes[votes.length - 1].description}</p>
              {renderVoteCounts(votes[votes.length - 1], votes.length - 1)}
            </div>
          </div>
        )
      ) : (
        <p>Žiadne hlasovanie ešte nebolo pridané.</p>
      )}
    </div>
  );
}

export default VotingBox;
