import React, { useState, useEffect } from "react";
import "./Modal.css";

function NewVoteModal({ isOpen, toggleModal }) {
  const [answers, setAnswers] = useState([""]); // Zaciname s jednym polickom odpovede
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  // Resetuje polia formulára pri zatvorení modalu
  useEffect(() => {
    if (!isOpen) {
      setTitle("");
      setDescription("");
      setAnswers([""]);
    }
  }, [isOpen]);

  // Ak modal nie je otvorený, nevykresluje komponent
  if (!isOpen) return null;

  // Funkcia na pridanie nového políčka pre odpoveď
  const addAnswerField = () => {
    setAnswers([...answers, ""]);
  };

  // Funkcia na sledovanie zmien v odpoveďových poliach
  const handleAnswerChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  // Funkcia na spracovanie odoslania formulára
  const [titleError, setTitleError] = useState("");
  const [descriptionError, setDescriptionError] = useState("");
  const [answerError, setAnswerError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    let isValid = true;

    if (!title.trim()) {
      setTitleError("Prosím, zadajte názov hlasovania.");
      isValid = false;
    } else {
      setTitleError("");
    }

    if (!description.trim()) {
      setDescriptionError("Prosím, zadajte popis hlasovania.");
      isValid = false;
    } else {
      setDescriptionError("");
    }

    const hasNonEmptyAnswer = answers.some((answer) => answer.trim() !== "");
    if (!hasNonEmptyAnswer) {
      setAnswerError("Prosím, zadajte aspoň jednu odpoveď.");
      isValid = false;
    } else {
      setAnswerError("");
    }

    if (!isValid) return;

    // Submit the form if valid
    setTitle("");
    setDescription("");
    setAnswers([""]);
    toggleModal(); // Close modal after successful submission
  };

  return (
    <div className="modal-overlay" onClick={toggleModal}>
      <div
        className="modal-content hlasovanie-modal"
        onClick={(e) => e.stopPropagation()} // Zabraňuje zatvoreniu modalu pri kliknutí vo vnútri
      >
        <h2>Pridať nové hlasovanie</h2>
        <form onSubmit={handleSubmit}>
          {/* Pole pre názov hlasovania */}
          <label>
            <input
              type="text"
              placeholder="Názov"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="input-field"
            />
            {titleError && <span className="error-message">{titleError}</span>}
          </label>

          {/* Pole pre popis hlasovania */}
          <label>
            <input
              type="text"
              placeholder="Popis hlasovania"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="input-field"
            />
          </label>

          {/* Dynamické polia pre odpovede */}
          {answers.map((answer, index) => (
            <label key={index}>
              <input
                type="text"
                placeholder={`Odpoved ${index + 1}`}
                value={answer}
                onChange={(e) => handleAnswerChange(index, e.target.value)}
                className="input-field"
              />
            </label>
          ))}

          {/* Tlačidlo na pridanie nového políčka pre odpoveď a tlačidlo na odoslanie */}
          <div className="button-container">
            <button
              type="button"
              className="add-answer-btn"
              onClick={addAnswerField}
            >
              <img
                src="./Plus_circle.svg"
                alt="Add Answer"
                className="add-answer-icon"
              />
            </button>
            <button type="submit" className="submit-btn">
              Odošli
            </button>
          </div>
        </form>

        {/* Tlačidlo na zatvorenie modalu */}
        <button className="close-btn" onClick={toggleModal}>
          X
        </button>
      </div>
    </div>
  );
}

export default NewVoteModal;
