import React, { useState, useEffect } from "react";
import "./Modal.css";
import "./Do"

function DocumentsModal({ isOpen, toggleModal, openInvoiceModal }) {
  const [description, setDescription] = useState("");
  const [type, setType] = useState("plán");
  const [date, setDate] = useState("");
  const [files, setFiles] = useState([]);

  // Reset fields when the modal is closed
  useEffect(() => {
    if (!isOpen) {
      setDescription("");
      setType("plán");
      setDate("");
      setFiles([]);
    }
  }, [isOpen]);

  const handleFileChange = (e) => {
    setFiles([...e.target.files]);
  };

  const handleTypeChange = (e) => {
    const selectedType = e.target.value;
    setType(selectedType);

    // Open InvoiceModal if "Faktúra" is selected
    if (selectedType === "faktúra") {
      toggleModal(); // Close DocumentsModal
      openInvoiceModal(); // Open InvoiceModal
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!description || !date || files.length === 0) {
      alert("Vyplňte všetky polia a pridajte súbor.");
      return;
    }
    console.log({ description, type, date, files });
    toggleModal(); // Close DocumentsModal
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={toggleModal}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="close-btn" onClick={toggleModal}>
          X
        </button>
        <h2>Pridať nový dokument</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Popis
            <input
              type="text"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="input-description"
              placeholder="Popis dokumentu"
              required
            />
          </label>
          <label>
            Typ
            <select
              value={type}
              onChange={handleTypeChange} // Handle type change here
              className="input-field"
              required
            >
              <option value="plán">Plán</option>
              <option value="záznam zo schôdze">Záznam zo schôdze</option>
              <option value="faktúra">Faktúra</option>
              {/* Add more options as needed */}
            </select>
          </label>
          <label>
            Dátum
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              className="input-date"
              required
            />
          </label>
          <label>
            Pridať súbor
            <input
              type="file"
              className="input-upload"
              onChange={handleFileChange}
              multiple
              required
            />
          </label>
          <div className="file-list">
            {files.length > 0 &&
              files.map((file, index) => <li key={index}>{file.name}</li>)}
          </div>
          <div className="button-container">
            <button type="submit" className="submit-btn">
              Uložiť
            </button>
            <button
              type="button"
              className="close-btn"
              onClick={toggleModal}
            ></button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default DocumentsModal;
