import React from "react";
import Header from "./Header"; // Assuming you already have a Header component
import Sidebar from "./Sidebar"; // Sidebar component
import "./Settings.css"; // Add a specific CSS file for Settings styling

function Settings() {
  const user = JSON.parse(localStorage.getItem("user")) || {}; // Fetch user details for the Header

  const handleLogout = () => {
    localStorage.removeItem("isAuthenticated");
    window.location.href = "/";
  };

  return (
    <div className="settings-page">
      <Sidebar />
      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        <div className="settings-content">
          <h1>Nastavenia</h1>
          <p>Tu môžete spravovať svoje nastavenia.</p>
        </div>
      </div>
    </div>
  );
}

export default Settings;
