import React, { useState, useEffect } from "react";
import "./VotingBox.css";

function VotingBox({ votes, toggleVoteModal, setVotes }) {
  const [expandedVoteIndex, setExpandedVoteIndex] = useState(null);
  const [voteCounts, setVoteCounts] = useState([]);
  const [hasVoted, setHasVoted] = useState(localStorage.getItem("hasVoted") === "true");
  const [userVote, setUserVote] = useState(null); // Na sledovanie hlasu používateľa

  // Načítanie indexu rozbaleného hlasovania zo storage
  useEffect(() => {
    const savedIndex = localStorage.getItem("expandedVoteIndex");
    if (savedIndex !== null) {
      setExpandedVoteIndex(JSON.parse(savedIndex));  // Načítame rozbalený index
    }

    // Načítame počty hlasov zo storage alebo ich inicializujeme
    const savedVoteCounts = localStorage.getItem("voteCounts");
    if (savedVoteCounts) {
      setVoteCounts(JSON.parse(savedVoteCounts));
    } else {
      const initialVoteCounts = votes.map(vote => new Array(vote.answers.length).fill(0));
      setVoteCounts(initialVoteCounts);
      localStorage.setItem("voteCounts", JSON.stringify(initialVoteCounts));
    }

    // Ak neexistuje hlasovanie, resetujeme všetky hodnoty
    if (votes.length === 0) {
      setVoteCounts([]);
      localStorage.removeItem("voteCounts");
      setHasVoted(false);
      localStorage.removeItem("hasVoted");
    }
  }, [votes]);

  // Funkcia na rozbalenie/zbalenie hlasovania
  const handleVoteClick = (index) => {
    const newExpandedIndex = expandedVoteIndex === index ? null : index;
    setExpandedVoteIndex(newExpandedIndex);
    localStorage.setItem("expandedVoteIndex", JSON.stringify(newExpandedIndex));
  };

  // Funkcia na hlasovanie
  const handleVote = (voteIndex, answerIndex) => {
    if (hasVoted) {
      alert("Už ste hlasovali!");
      return;
    }

    const updatedVoteCounts = [...voteCounts];
    if (!updatedVoteCounts[voteIndex]) {
      updatedVoteCounts[voteIndex] = new Array(votes[voteIndex].answers.length).fill(0);
    }

    updatedVoteCounts[voteIndex][answerIndex] += 1;
    setUserVote({ voteIndex, answerIndex }); // Zapamätáme si, na ktorú odpoveď používateľ hlasoval

    setVoteCounts(updatedVoteCounts);
    localStorage.setItem("voteCounts", JSON.stringify(updatedVoteCounts));
    localStorage.setItem("hasVoted", "true");
    setHasVoted(true);
  };

  // Funkcia na vymazanie hlasu
  const handleRemoveVote = () => {
    if (!userVote) return;

    const { voteIndex, answerIndex } = userVote;

    const updatedVoteCounts = [...voteCounts];
    updatedVoteCounts[voteIndex][answerIndex] -= 1;

    setVoteCounts(updatedVoteCounts);
    localStorage.setItem("voteCounts", JSON.stringify(updatedVoteCounts));

    setUserVote(null); // Vymazanie uloženého hlasu
    localStorage.removeItem("hasVoted"); // Odstránenie stavu hlasovania
    setHasVoted(false);
  };

  // Funkcia na vymazanie hlasovania (vymazanie aj počtov hlasov)
  const deleteVote = (voteIndex) => {
    // Odstránenie hlasovania
    const updatedVotes = votes.filter((_, index) => index !== voteIndex);
    setVotes(updatedVotes);
    localStorage.setItem("votes", JSON.stringify(updatedVotes));

    // Odstránenie počtov hlasov pre dané hlasovanie
    const updatedVoteCounts = voteCounts.filter((_, index) => index !== voteIndex);
    setVoteCounts(updatedVoteCounts);
    localStorage.setItem("voteCounts", JSON.stringify(updatedVoteCounts));

    // Resetovanie stavu, že používateľ už hlasoval
    setUserVote(null); // Vymažte hlas používateľa
    localStorage.removeItem("hasVoted");

    // Ak už nie je žiadne hlasovanie, resetujte všetky dáta
    if (updatedVotes.length === 0) {
      localStorage.removeItem("voteCounts");
      setVoteCounts([]);
    }
  };

  const latestVote = votes[votes.length - 1];

  // Funkcia na pridanie nového hlasovania
  const addNewVote = (newVote) => {
    const updatedVotes = [...votes, newVote];
    setVotes(updatedVotes);
    localStorage.setItem("votes", JSON.stringify(updatedVotes));

    // Pridanie nových počtov hlasov pre nové hlasovanie
    const updatedVoteCounts = [...voteCounts, new Array(newVote.answers.length).fill(0)];
    setVoteCounts(updatedVoteCounts);
    localStorage.setItem("voteCounts", JSON.stringify(updatedVoteCounts));
  };

  return (
    <div className="voting-box">
      <div className="voting-header">
        <h3>Posledné hlasovanie</h3>
        <button className="add-btn" onClick={toggleVoteModal}>
          <img src="./Add.svg" alt="Add Vote" className="add-icon" />
        </button>
      </div>
      <hr />
      {votes.length > 0 ? (
        votes.map((vote, index) => (
          <div key={index}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
              <h4
                onClick={() => handleVoteClick(index)} // Kliknutím na názov sa hlasovanie rozbalí/zbalí
                style={{ cursor: "pointer", color: "blue" }}
              >
                {vote.title}
              </h4>
              <button
                onClick={() => deleteVote(index)} // Vymazanie hlasovania
                style={{
                  background: "red",
                  color: "white",
                  border: "none",
                  padding: "5px 10px",
                  cursor: "pointer"
                }}
              >
                Vymazať
              </button>
            </div>

            {/* Ak je hlasovanie rozbalené, zobrazí sa popis a odpovede */}
            {expandedVoteIndex === index && (
              <div>
                <p>{vote.description}</p>
                <ul className="voting-results">
                  {vote.answers.map((answer, answerIndex) => (
                    <li key={answerIndex}>
                      <button
                        onClick={() => handleVote(index, answerIndex)} // Na kliknutie hlasujeme
                        style={{ marginRight: "10px" }}
                        disabled={hasVoted} // Zablokovanie tlačidla, ak používateľ už hlasoval
                      >
                        {answer}
                      </button>
                      {/* Zobrazenie počtu hlasov pre každú odpoveď */}
                      {voteCounts[index] && voteCounts[index][answerIndex] !== undefined ? (
                        <span>{voteCounts[index][answerIndex]} hlasov</span>
                      ) : (
                        <span>0 hlasov</span>
                      )}
                      {/* Zobrazenie, že používateľ už hlasoval za danú možnosť */}
                      {userVote && userVote.voteIndex === index && userVote.answerIndex === answerIndex && (
                        <span style={{ color: "green", marginLeft: "10px" }}>
                          Tento hlas ste už odovzdali
                        </span>
                      )}
                    </li>
                  ))}
                </ul>

                {/* Tlačidlo na vymazanie hlasu */}
                {userVote && (
                  <button
                    onClick={handleRemoveVote}
                    style={{
                      marginTop: "10px",
                      backgroundColor: "orange",
                      color: "white",
                      padding: "8px 15px",
                      border: "none",
                      cursor: "pointer",
                    }}
                  >
                    Vymazať môj hlas
                  </button>
                )}
              </div>
            )}
          </div>
        ))
      ) : (
        <p>Žiadne hlasovanie ešte nebolo pridané.</p>
      )}
    </div>
  );
}

export default VotingBox;
