import React, { useState, useEffect } from "react";
import Header from "./Header";
import Sidebar from "./Sidebar";
import "./Settings.css";

function Settings() {
  const user = JSON.parse(localStorage.getItem("user")) || {};
  const [currentEmail, setCurrentEmail] = useState(user.email || "");
  const [formData, setFormData] = useState({
    newEmail: "",
    oldPassword: "",
    newPassword: "",
    confirmPassword: "",
  });

  useEffect(() => {
    if (user && user.email) {
      setCurrentEmail(user.email);
    }
  }, [user]);

  const handleLogout = () => {
    localStorage.removeItem("user");
    window.location.href = "/";
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { newEmail, oldPassword, newPassword, confirmPassword } = formData;

    // Validate oldPassword (must be non-empty)
    if (!oldPassword) {
      alert("Staré heslo je potrebné na uloženie zmien!");
      return;
    }

    // Validate newPassword and confirmPassword
    if (newPassword && newPassword !== confirmPassword) {
      alert("Heslá sa nezhodujú!");
      return;
    }

    // Validate newEmail if it is provided (basic email regex)
    if (newEmail && !/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(newEmail)) {
      alert("Neplatný formát e-mailu.");
      return;
    }

    try {
      const response = await fetch("/api/user/update", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ oldPassword, newEmail, newPassword }), // Ensure this matches your backend
      });

      const data = await response.json();

      if (data.success) {
        alert("Údaje boli úspešne aktualizované.");
        setFormData({
          newEmail: "",
          oldPassword: "",
          newPassword: "",
          confirmPassword: "",
        });
      } else {
        alert(data.message || "Chyba pri aktualizácii údajov.");
      }
    } catch (error) {
      console.error("Error updating user data:", error);
      alert("Chyba pri komunikácii so serverom.");
    }
  };

  return (
    <div className="settings-page">
      <Sidebar />
      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        <div className="settings-content">
          <div className="settings-box">
            <h1>Nastavenia</h1>
            <form className="settings-form" onSubmit={handleSubmit}>
              <div className="form-group">
                <div className="label-wrapper">
                  <label className="label-text">Aktuálny e-mail:</label>
                  <div className="current-email">{currentEmail}</div>
                </div>
                <div className="label-wrapper">
                  <label className="label-text new-email">Nový e-mail:</label>
                  <input
                    type="email"
                    name="newEmail"
                    value={formData.newEmail}
                    onChange={handleInputChange}
                    className="input-field"
                    placeholder="Zadajte nový e-mail"
                  />
                </div>
              </div>
              <div className="form-group">
                <div className="label-wrapper">
                  <label className="label-text">Nové heslo:</label>
                  <input
                    type="password"
                    name="newPassword"
                    value={formData.newPassword}
                    onChange={handleInputChange}
                    className="input-field"
                    placeholder="Zadajte nové heslo"
                  />
                </div>
                <div className="label-wrapper">
                  <label className="label-text">Potvrdenie hesla:</label>
                  <input
                    type="password"
                    name="confirmPassword"
                    value={formData.confirmPassword}
                    onChange={handleInputChange}
                    className="input-field"
                    placeholder="Zopakujte nové heslo"
                  />
                </div>
              </div>
              <div className="label-wrapper">
                <label className="label-text">Staré heslo:</label>
                <input
                  type="password"
                  name="oldPassword"
                  value={formData.oldPassword}
                  onChange={handleInputChange}
                  className="input-field"
                  placeholder="Zadajte staré heslo"
                />
              </div>
              <button type="submit" className="save-btn">
                Uložiť
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Settings;
