import React, { useState, useEffect } from "react";
import "./VotingBox.css";

function VotingBox({ votes, toggleVoteModal }) {
  // Načítame index rozbaleného hlasovania zo localStorage, ak existuje
  const [expandedVoteIndex, setExpandedVoteIndex] = useState(null);

  // Stav pre počet hlasov pre každú odpoveď v hlasovaní
  const [voteCounts, setVoteCounts] = useState([]);

  // Načítame zo storage stav hlasovania a počty hlasov
  useEffect(() => {
    const savedIndex = localStorage.getItem("expandedVoteIndex");
    if (savedIndex !== null) {
      setExpandedVoteIndex(JSON.parse(savedIndex));  // Načítame rozbalený index zo storage
    }

    const savedVoteCounts = localStorage.getItem("voteCounts");
    if (savedVoteCounts !== null) {
      setVoteCounts(JSON.parse(savedVoteCounts));  // Načítame počty hlasov zo storage
    }
  }, []);

  // Funkcia na rozbalenie alebo zbalenie hlasovania
  const handleVoteClick = (index) => {
    const newExpandedIndex = expandedVoteIndex === index ? null : index;
    setExpandedVoteIndex(newExpandedIndex);
    localStorage.setItem("expandedVoteIndex", JSON.stringify(newExpandedIndex));
  };

  // Funkcia na hlasovanie
  const handleVote = (voteIndex, answerIndex) => {
    const updatedVoteCounts = [...voteCounts];
    updatedVoteCounts[voteIndex][answerIndex] += 1;  // Zväčšíme počet hlasov pre túto odpoveď

    setVoteCounts(updatedVoteCounts);
    localStorage.setItem("voteCounts", JSON.stringify(updatedVoteCounts));  // Uložíme nové počty hlasov
  };

  const latestVote = votes[votes.length - 1]; // Získame posledné hlasovanie

  return (
    <div className="voting-box">
      <div className="voting-header">
        <h3>Posledné hlasovanie</h3>
        <button className="add-btn" onClick={toggleVoteModal}>
          <img src="./Add.svg" alt="Add Vote" className="add-icon" />
        </button>
      </div>
      <hr />
      {latestVote ? (
        <div>
          <h4
            onClick={() => handleVoteClick(0)} // Kliknutím na názov sa hlasovanie rozbalí/zbalí
            style={{ cursor: "pointer", color: "blue" }}
          >
            {latestVote.title}
          </h4>

          {/* Ak je hlasovanie rozbalené, zobrazí sa popis a odpovede */}
          {expandedVoteIndex === 0 && (
            <div>
              <p>{latestVote.description}</p>
              <ul className="voting-results">
                {latestVote.answers.map((answer, index) => (
                  <li key={index}>
                    <button
                      onClick={() => handleVote(0, index)} // Na kliknutie hlasujeme
                      style={{ marginRight: "10px" }}
                    >
                      {answer}
                    </button>
                    {/* Zobrazenie počtu hlasov pre každú odpoveď */}
                    {voteCounts[0] && voteCounts[0][index] !== undefined ? (
                      <span>{voteCounts[0][index]} hlasov</span>
                    ) : (
                      <span>0 hlasov</span>
                    )}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      ) : (
        <p>Žiadne hlasovanie ešte nebolo pridané.</p>
      )}
    </div>
  );
}

export default VotingBox;
