// Dokumenty.js
import React, { useEffect, useState } from "react";
import Sidebar from "./Sidebar";
import Header from "./Header";
import "./Dokumenty.css";

const Dokumenty = () => {
  const [documents, setDocuments] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "asc" }); // Sorting state
  const user = JSON.parse(localStorage.getItem("user")) || {};

  useEffect(() => {
    const mockDocuments = [
      {
        id: 1,
        name: "Faktura_Januar",
        popis: "January invoice for utilities.",
        type: "faktura",
        fileType: ".pdf",
        date: "2024-01-15",
        url: "/files/faktura_januar.pdf",
      },
      {
        id: 2,
        name: "Zaznam_Schodza",
        popis: "Meeting notes from January 2023.",
        type: "zaznam zo schodze",
        fileType: ".txt",
        date: "2023-01-20",
        url: "/files/zaznam_schodza_jan.txt",
      },
      {
        id: 3,
        name: "Faktura_Februar",
        popis: "February invoice for utilities.",
        type: "faktura",
        fileType: ".pdf",
        date: "2024-02-10",
        url: "/files/faktura_februar.pdf",
      },
    ];

    setDocuments(mockDocuments);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("isAuthenticated");
    window.location.href = "/";
  };

  // Sorting logic
  const sortDocuments = (key) => {
    const direction =
      sortConfig.key === key && sortConfig.direction === "asc" ? "desc" : "asc";
    const sortedDocs = [...documents].sort((a, b) => {
      if (a[key] < b[key]) return direction === "asc" ? -1 : 1;
      if (a[key] > b[key]) return direction === "asc" ? 1 : -1;
      return 0;
    });

    setDocuments(sortedDocs);
    setSortConfig({ key, direction });
  };

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        <div className="dokumenty-content">
          <h1>Dokumenty</h1>
          <table className="documents-table">
            <thead>
              <tr>
                <th onClick={() => sortDocuments("name")}>Name</th>
                <th onClick={() => sortDocuments("popis")}>Popis</th>
                <th onClick={() => sortDocuments("type")}>Type</th>
                <th onClick={() => sortDocuments("fileType")}>File Type</th>
                <th onClick={() => sortDocuments("date")}>Date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {documents.map((doc) => (
                <tr key={doc.id}>
                  <td>{doc.name}</td>
                  <td>{doc.popis}</td>
                  <td>{doc.type}</td>
                  <td>{doc.fileType}</td>
                  <td>{doc.date}</td>
                  <td>
                    <a href={doc.url} target="_blank" rel="noopener noreferrer">
                      View
                    </a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Dokumenty;
