import React, { useState } from "react";
import Header from "./Header";
import Sidebar from "./Sidebar";
import "./Settings.css";

function Settings() {
  const user = JSON.parse(localStorage.getItem("user")) || {};
  const [currentEmail, setCurrentEmail] = useState(user.email || "");
  const [newEmail, setNewEmail] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleLogout = () => {
    localStorage.removeItem("isAuthenticated");
    window.location.href = "/";
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!oldPassword) {
      alert("Staré heslo je potrebné na uloženie zmien!");
      return;
    }

    if (newEmail) {
      console.log("Old Password:", oldPassword);
      console.log("Email updated to:", newEmail);
      alert("E-mail bol úspešne zmenený.");
      setNewEmail("");
    }

    if (newPassword || confirmPassword) {
      if (newPassword !== confirmPassword) {
        alert("Heslá sa nezhodujú!");
        return;
      }
      console.log("Old Password:", oldPassword);
      console.log("Password updated successfully.");
      alert("Heslo bolo úspešne zmenené.");
      setNewPassword("");
      setConfirmPassword("");
    }

    setOldPassword("");
  };

  return (
    <div className="settings-page">
      <Sidebar />
      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        <div className="settings-content">
          <h1>Nastavenia</h1>
          <form className="settings-form" onSubmit={handleSubmit}>
            <div className="form-row">
              <div className="form-group">
                <h2>Zmena e-mailu</h2>
                <div className="current-email">
                  <strong>Aktuálny e-mail:</strong> {currentEmail}
                </div>
                <label>
                  Nový e-mail
                  <input
                    type="email"
                    value={newEmail}
                    onChange={(e) => setNewEmail(e.target.value)}
                    className="input-field"
                    placeholder="Zadajte nový e-mail"
                  />
                </label>
              </div>
              <div className="form-group">
                <h2>Zmena hesla</h2>
                <label>
                  Nové heslo
                  <input
                    type="password"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    className="input-field"
                    placeholder="Zadajte nové heslo"
                  />
                </label>
                <label>
                  Potvrdenie hesla
                  <input
                    type="password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    className="input-field"
                    placeholder="Zopakujte nové heslo"
                  />
                </label>
              </div>
            </div>
            <div className="shared-section">
              <label>
                Staré heslo
                <input
                  type="password"
                  value={oldPassword}
                  onChange={(e) => setOldPassword(e.target.value)}
                  className="input-field"
                  placeholder="Zadajte staré heslo"
                  required
                />
              </label>
              <button type="submit" className="save-btn">
                Uložiť
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Settings;
