import React, { useEffect, useState } from 'react';

function ProblemsBox() {
    const [problems, setProblems] = useState([]);
    const [newProblem, setNewProblem] = useState('');

    // Fetch tasks from the database on component mount
    useEffect(() => {
        fetch('http://localhost:3003/api/problems')
            .then((res) => res.json())
            .then((data) => setProblems(data))
            .catch((error) => console.error('Error fetching tasks:', error));
    }, []);

    // Handler to add a new problem
    const addProblem = () => {
        if (!newProblem) return;

        fetch('http://localhost:3003/api/problems', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ problem: newProblem }),
        })
            .then((res) => res.json())
            .then((newTask) => {
                setProblems([...problems, newTask]);
                setNewProblem(''); // Clear input
            })
            .catch((error) => console.error('Error adding task:', error));
    };

    // Handler to delete a task
    const deleteProblem = (id) => {
        fetch(`http://localhost:3003/api/problems/${id}`, { method: 'DELETE' })
            .then(() => setProblems(problems.filter((problem) => problem.id !== id)))
            .catch((error) => console.error('Error deleting task:', error));
    };

    return (
        <div className="problems-box">
            <div className="problems-header">
                <h3>Problémy</h3>
                <button className="add-btn" onClick={addProblem}>
                    <img src="./Add.svg" alt="Add Icon" className="add-icon" />
                </button>
            </div>
            <hr />
            <input
                type="text"
                value={newProblem}
                onChange={(e) => setNewProblem(e.target.value)}
                placeholder="Nový problém"
            />

            <ul className="problems-list">
                {problems.map((problem) => (
                    <li key={problem.id}>
                        {problem.problem}
                        <button className="delete-btn" onClick={() => deleteProblem(problem.id)}>
                            <img src="./Trashcan.svg" alt="Delete Icon" className="delete-icon" />
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ProblemsBox;
