import React, { useState, useEffect } from "react";
import "./Modal.css";

function NewVoteModal({ isOpen, toggleModal }) {
  const [votes, setVotes] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [answers, setAnswers] = useState([""]); // Starts with one answer field

  // Fetch existing votes from the backend
  useEffect(() => {}, [isOpen]);

  // Add a new vote
  const addVote = () => {
    const voteData = {
      title,
      description,
      answers: answers.filter((answer) => answer.trim() !== ""), // Remove empty answers
    };

    fetch("http://localhost:3003/api/votes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(voteData),
    })
      .then((res) => res.json())
      .then((addedVote) => {
        setVotes([...votes, addedVote]); // Adds the new vote locally
        setTitle(""); // Clear title field
        setDescription(""); // Clear description field
        setAnswers([""]); // Reset answers field
        toggleModal(); // Close modal after submission
      })
      .catch((error) => console.error("Error adding vote:", error));
  };

  // Mark vote as completed
  const markVoteAsCompleted = (id) => {
    const confirmComplete = window.confirm(
      "Určite chcete označiť hlasovanie za ukončené?"
    );
    if (!confirmComplete) return;

    fetch(`http://localhost:3003/api/votes/${id}`, { method: "PUT" })
      .then(() => {
        setVotes(votes.filter((vote) => vote.id !== id)); // Remove the completed vote locally
      })
      .catch((error) =>
        console.error("Error marking vote as completed:", error)
      );
  };

  // Add new answer field
  const addAnswerField = () => {
    setAnswers([...answers, ""]);
  };

  // Handle changes in answer fields
  const handleAnswerChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  // Prevent form submission if fields are empty
  const handleSubmit = (e) => {
    e.preventDefault();
    if (
      !title ||
      !description ||
      answers.every((answer) => answer.trim() === "")
    ) {
      alert("Prosím, vyplňte všetky polia hlasovania.");
      return;
    }
    addVote();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={toggleModal}>
      <div
        className="modal-content hlasovanie-modal"
        onClick={(e) => e.stopPropagation()}
      >
        <h2>Pridať nové hlasovanie</h2>
        <form onSubmit={handleSubmit}>
          <label>
            <input
              type="text"
              placeholder="Názov"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="input-field"
            />
          </label>
          <label>
            <input
              type="text"
              placeholder="Popis hlasovania"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="input-field"
            />
          </label>

          {answers.map((answer, index) => (
            <label key={index}>
              <input
                type="text"
                placeholder={`Odpoved ${index + 1}`}
                value={answer}
                onChange={(e) => handleAnswerChange(index, e.target.value)}
                className="input-field"
              />
            </label>
          ))}
          <div className="button-container">
            <button
              type="button"
              className="add-answer-btn"
              onClick={addAnswerField}
            >
              <img
                src="./Plus_circle.svg"
                alt="Add Answer"
                className="add-answer-icon"
              />
            </button>
            <button type="submit" className="submit-btn">
              Odošli
            </button>
          </div>
        </form>
        <button className="close-btn" onClick={toggleModal}>
          X
        </button>

        {/* List of existing votes */}
        <h3>Aktuálne hlasovania</h3>
        <ul className="votes-list">
          {votes.map((vote) => (
            <li key={vote.id} className="vote-item">
              <span>{vote.title}</span>
              <button
                className="complete-btn"
                onClick={() => markVoteAsCompleted(vote.id)}
              >
                <img
                  src="./Complete.svg"
                  alt="Označiť ako dokončené"
                  className="complete-icon"
                />
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default NewVoteModal;
