import React, { useState, useEffect } from "react";
import "./VotingBox.css";

function VotingModal({ isOpen, toggleVoteModal }) {
  // Stavové premenné pre polia formulára
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [answers, setAnswers] = useState(["", ""]); // Štart s dvoma odpoveďami ako predvolený počet

  // Resetuje polia formulára, keď sa modal zatvorí
  useEffect(() => {
    if (!isOpen) {
      setTitle("");
      setDescription("");
      setAnswers(["", ""]);
    }
  }, [isOpen]);

  // Funkcia na odoslanie formulára
  const handleSubmit = (e) => {
    e.preventDefault();
    // Logika pre odoslanie formulára sem...

    // Resetuje polia po odoslaní
    setTitle("");
    setDescription("");
    setAnswers(["", ""]);
    toggleVoteModal(); // Zatvorí modal
  };

  // Funkcia na pridanie nového poľa pre odpoveď
  const addAnswerField = () => {
    setAnswers([...answers, ""]);
  };

  // Funkcia na sledovanie zmien v odpoveďových poliach
  const handleAnswerChange = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  // Ak modal nie je otvorený, nevykreslí komponent
  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={toggleVoteModal}>
      <div
        className="modal-content hlasovanie-modal"
        onClick={(e) => e.stopPropagation()}
      >
        <h2>Pridať nové hlasovanie</h2>
        <form onSubmit={handleSubmit}>
          {/* Názov hlasovania */}
          <label>
            <input
              type="text"
              placeholder="Názov"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="input-title"
            />
          </label>

          {/* Popis hlasovania */}
          <label>
            <input
              type="text"
              placeholder="Popis hlasovania"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="input-description"
            />
          </label>

          {/* Polia pre odpovede */}
          {answers.map((answer, index) => (
            <label key={index}>
              <input
                type="text"
                placeholder={`Odpoved ${index + 1}`}
                value={answer}
                onChange={(e) => handleAnswerChange(index, e.target.value)}
                className="input-answer"
              />
            </label>
          ))}

          {/* Tlačidlo na pridanie nového poľa pre odpoveď a tlačidlo na odoslanie */}
          <div className="button-container">
            <button
              type="button"
              className="add-answer-btn"
              onClick={addAnswerField}
            >
              <img
                src="./Add.svg"
                alt="Add Answer"
                className="add-answer-icon"
              />
            </button>
            <button type="submit" className="submit-btn">
              Odošli
            </button>
          </div>
        </form>

        {/* Tlačidlo na zatvorenie modalu */}
        <button className="close-btn" onClick={toggleVoteModal}>
          X
        </button>
      </div>
    </div>
  );
}

export default VotingModal;
